var yo = require('yo-yo')
var csjs =require('csjs-inject')

module.exports = anotherBox

css = csjs`
  .panel {
    border: 1px solid yellow;
    background-color: ${'#ff0000'};
  }
  .title {
    padding: 4px;
    font-size: 35px;
  }
  .dashboard {
    font-weight: 900;
    font-size: 20px;
    padding: 20px;
  }
`

function anotherBox ({ data: { name: name = 'anonymous' } = {} } = {}) {
// function anotherBox (params) {
//   var name  = ((params||{}).data ||{}).name  || 'anonymous'


  var counter = 0
  var condition1
  var xxx = [ condition1 ? css.panel : css.panel + ' ' + css.dashboard ]

  condition1 ? xxx.push() : undefined

  function template (name) {
    return yo`
      <div class=${xxx.join('')}>
        <h1 class=${css.title}>
          hello ${name}!
        </h1>
        <div class="${css.dashboard}">${counter}</div>
        <button onclick=${log}> press </button>
      </div>
    `
  }
  var tree = template(name)
  return tree
  function log (event) {
    counter++
    yo.update(tree, template(name))
  }
}
