var yo = require('yo-yo')
var csjs =require('csjs-inject')

module.exports = welcomeBox


function welcomeBox ({
  theme : { color : color = 'red' }       = {},
  data  : { name  : name  = 'anonymous' } = {}
} = {}) {
// function welcomeBox (params) {
//   var color = ((params||{}).theme||{}).color || 'red'
//   var name  = ((params||{}).data ||{}).name  || 'anonymous'

  var styles = csjs`
    .panel {
      border: 1px solid black;
      background-color: ${color};
    }
    .title {
      padding: 4px;
      font-size: 35px;
    }
  `
  return yo`
    <div class=${styles.panel}>

      <h1 class=${styles.title}>
        hello ${name}!
      </h1>

    </div>
  `
}
